                        email = user.email;
                        photo = user.photoURL;
                        emailVerified = user.emailVerified;
                        uid = user.uid;



                return  (

        

                    <Fragment>

                    <ListItem button style={{borderRadius:'20px'}} >

                        <div style={{display:'flex', flexDirection:'row', alin}} > 

                                    <div>

                                            
                                            <Avatar alt="Remy Sharp" src="#" style={{boxShadow:'2px 2px 2px rgb(100, 100, 100)'}} />
                                          

                                    </div>

    
                                     <div >
                            
                                            <h5 style={{color:'grey', fontFamily:'Roboto, sans-serif', fontSize:'15px'}}> {props.name}</h5>
                            
