import React, { Fragment, useState, useEffect } from 'react';
import Avatar from '@material-ui/core/Avatar';

import { IconButton } from '@material-ui/core';
import DonutLargeIcon from '@material-ui/icons/DonutLarge';
import SearchIcon from '@material-ui/icons/Search';
import AttachFileIcon from '@material-ui/icons/AttachFile';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Form from 'react-bootstrap/Form'
import Button from '@material-ui/core/Button';
import {db} from '../FirebaseDb.js';
import firebase from "firebase";

import {useParams} from "react-router-dom"


const Conversation = () =>{


                            var user = firebase.auth().currentUser;
                            var name, email, photo, uid, emailVerified;

                            name = user.displayName;
                            email = user.email;
                            photo = user.photoURL;
                            emailVerified = user.emailVerified;
                            uid = user.uid;

    

                            const {roomId} =useParams();
                            
                            const [message, setMessage]=useState([]);

                            const [input, setInput]=useState("");

                            


           
      

                                const sendMessage = (e) => {
                                    e.preventDefault();
                                    db.collection('rooms').doc(roomId).collection("message").add({
                                        message:input,
                                        name:name, 
                                        timestamp:firebase.firestore.FieldValue.serverTimestamp(),
                                    });
                                    console.log("escribiste", input);
                                    setInput("");
                                };


                         const [roomName, setRoomName]=useState("");

                        useEffect(() =>{
                            if (roomId!=="") {
                                db.collection('rooms').doc(roomId).onSnapshot(snapshot=>(
                                    setRoomName(snapshot.data().name)
                                ));
                                db.collection("rooms")
                                .doc(roomId)
                                .collection("message")
                                .orderBy("timestamp","asc")
                                .onSnapshot((snapshot)=>
                                setMessage(snapshot.docs.map((doc)=>
                                doc.data()))
                                );
                            }
                        },[roomId])










    return(

        <Fragment>

            

            {message.map(message =>(
                
            <div  className={`messageConvesacion ${message.name===name && "messageConvesacion_R"}`} id="ctnConversacion" >
             
             <div id="userAnsMessage">

             <Avatar alt="Travis Howard" src={message.myPic} style={{width:'40px', height:'40px',marginBottom:'10px',boxShadow:'2px 2px 2px rgb(100, 100, 100)'}}/>
             
             <p style={{fontSize:'10px', marginLeft:'10px'}}>{new Date(message.timestamp?.toDate()).toUTCString()}</p>

             </div>

            
  
             <p id="mensaje">{message.message}</p>


            </div>
            ))}

           

            
        </Fragment>
        

    );
}

export default Conversation;
