import React, {Fragment , useState, useEffect} from 'react';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import firebase from "firebase";
import {db} from '../FirebaseDb.js';
import SidebarConversations from './sidebarConversations'
import Conversation from './conversation';
import WriteMessage from './writeMessage.jsx';
import Fab from '@material-ui/core/Fab';
import ChatIcon from '@material-ui/icons/Chat';
import { Modal } from 'semantic-ui-react'
import Button from '@material-ui/core/Button';
import { Input } from 'semantic-ui-react'
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';




import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
  } from "react-router-dom";
import RoomBuilder from './roombuilder.jsx';
import FinishConversation from './finishConversation.jsx';
import NoConversationYet from './noConversationYet.jsx';
import CurrentRoom from './currentRoom.jsx';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';




const Messenger = () =>{

                        const [openAviso, setOpenAviso] = React.useState(false);

                        const handleClickOpen = () => {
                        setOpenAviso(true);
                        };

                        const handleClose = () => {
                        setOpenAviso(false);
                        };

                    const [open, setOpen] = React.useState(false)

                    const [nameG, setNameG] = useState("")



                    var user = firebase.auth().currentUser;
                    var name, email, photo, uid, emailVerified;

                    name = user.displayName;
                    email = user.email;
                    photo = user.photoURL;
                    emailVerified = user.emailVerified;
                    uid = user.uid;

                    const [ grabar, setGrabar] = useState([]);

                    useEffect(() =>{

                        const unsubscribe = db.collection(`integrantesTemporal${email}`).onSnapshot(snapshot =>(
      
                        setGrabar(snapshot.docs.map(doc =>
                        ({
      
                        id:doc.id,
                        data:doc.data(),
      
                        }))
                        )
                        ));
      
                            return () =>{
                                unsubscribe();
                                
                            }
      
                            
      
                        }, [])


                    const [seleccion, setSeleccion]=useState([])
                    useEffect(() =>{

                        const unsubscribe = db.collection(`integrantesTemporal${email}`).onSnapshot(snapshot =>(

                        setSeleccion(snapshot.docs.map(doc =>
                        (

                        doc.data().integrantes

                        ))
                        )
                        ));

                            return () =>{
                                unsubscribe();
                                
                            }

                            

                        }, [])

                        const [seleccionPase, setSeleccionPase]=useState([])
                         useEffect(() =>{

                        const unsubscribe = db.collection(`integrantesTemporal${email}`).onSnapshot(snapshot =>(

                        setSeleccionPase(snapshot.docs.map(doc =>
                        (

                        doc.data().integrantesPase

                        ))
                        )
                        ));

                            return () =>{
                                unsubscribe();
                                
                            }

                            

                        }, [])

                    const [rooms, setRooms]=useState([]);

                    useEffect(() =>{

                            const unsubscribe = db.collection('rooms').onSnapshot(snapshot =>(

                            setRooms(snapshot.docs.map(doc =>
                            ({

                            id:doc.id,
                            data:doc.data(),

                            }))
                            )
                            ));

                                return () =>{
                                    unsubscribe();
                                }

                   }, [])



                   const [usuarios, setUsuarios]= useState([]);

                   const [profile, setProfile] = useState ([]);

                        React.useEffect(() =>{
                    
                                db.collection('Friends').doc(email).collection('myFriends').onSnapshot(snapshot=>(
                                    setUsuarios(snapshot.docs.map((doc)=>
                                    doc.data()))
                                ))

                               
                                


                                db.collection(`perfil${email}`).onSnapshot(snapshot=>(
                                    setProfile(snapshot.docs.map((doc)=>
                                    doc.data()))

                                ))
                    
                          },[]);

                          
                          const cleanIntegrantes = () =>{

                            grabar.map(grabar=>(
                
                              db.collection(`integrantesTemporal${email}`).doc(grabar.id).delete().then(function() {
                                console.log("Document successfully deleted!");
                                }).catch(function(error) {
                                    console.error("Error removing document: ", error);
                                })
                
                
                            ))
                           
                          }




                          const createChat = () =>{
                 
                             db.collection("rooms").doc().set({
                                 admin:email,
                                 name: nameG,
                                 integrantes:seleccion,
                                 integrantesPase:seleccionPase
                                 
                             })

                             setNameG("")
                             setOpen(false)
                 
                        
                          }; 


                         


    return(

        <Fragment>


             <div style={{marginLeft:'0px'}}>



                        <Modal

                                onClose={() => setOpen(false)}
                                onOpen={() => setOpen(true)}
                                open={open}
                                //trigger={<Button>Show Modal</Button>}
                                style={{width:'700px',height:'580px',marginTop:'0px',marginLeft:'30%', padding:'30px', backgroundColor:'rgb(241, 240, 240)', borderRadius:'20px'}}
                                >
                               
                                </Modal>

                        

            <Router>

            <div style={{backgroundColor:'#e7e7e7', marginLeft:'0px', marginTop:'0px', height:'90vh', width:'100%'}}>

            
               <br/>

                <div style={{display:'flex', flexDirection:'row'}}>

                    <div>
                        <Paper style={{width:'250px',height:'540px',marginLeft:'70px', borderRadius:'20px', overflow:'auto', padding:'10px', backgroundColor:'#f5f5f5'}}>

                            <br/>

                        <div style={{display:'flex', flexDirection:'Row', alignItems:'center', justifyContent:'space-around' }}>

                            <div>
                            <p style={{fontSize:'20px',textAlign:'center' ,fontFamily:'Roboto, sans-serif'}}>Conversaciones</p>
                            </div>

                            <div>
                            <Fab color="primary" aria-label="add" style={{marginLeft:'0px', marginBottom:'0px',width:'40px', height:'40px'}} onClick={() => setOpen(true)} >
                            <ChatIcon/>
                            </Fab>
                            </div>
                        </div>

                        <br/>

                        <Divider />

                        <div>
                        
                        <List>
        
                                {rooms.map(room =>(

                                <div style={{display:'flex', flexDirection:'row', alignItems:'center', justifyContent:'space-between', marginBottom:'10px'}}>



                                    <Paper elevation={1} style={{width:'180px', borderRadius:'20px',backgroundColor:'#1565c0'}}>
                                        <Link to={`/rooms/${room.id}`}>
                                        

                                        <SidebarConversations key={room.id} id={room.id} name={room.data.name} integrantes={room.data.integrantes} integrantesPase={room.data.integrantesPase} admin={room.data.admin} />

                                        

                                        </Link>

                                    </Paper>


                                    <div style={{marginBottom:'0px',marginLeft:'0px'}}>

                                       
                                       <FinishConversation id={room.id} integrantesPase={room.data.integrantesPase} admin={room.data.admin}/>


                                     </div>
                                    
                                </div>

                                ))}

                        </List>

                        </div>

                        </Paper>
                       
                    </div>

                    <Switch>
                    <div>

                    

                    <Paper elevation={3} style={{width:'800px',height:'480px',marginLeft:'40px', backgroundColor:'#f5f5f5', overflow:'auto', padding:'10px', borderRadius:'20px' }}>

                        <Route exact path="/rooms/:roomId" children={<CurrentRoom/> }/>
                        
                        <p style={{fontSize:'18px', fontFamily:'Roboto, sans-serif', marginLeft:'20px'}}></p>

                        <div style={{ height:'80vh', padding:'20px'}}>

                        <Route exact path="/rooms/:roomId" children={<Conversation/> }/>

                        <Route exact path="/messenger" component= {NoConversationYet}/>

                        
                        </div>

                    

                    </Paper>

                    <div style={{width:'800px',height:'43px',marginLeft:'40px', marginTop:'14px'}}>


                        <div>

                              {profile.length!==0?
                         
                                    profile.map(profile=>(
                   
                                        <Route exact path="/rooms/:roomId" children={<WriteMessage  myPic={profile.profilePic}/>}/>

                                    ))

                                    :
                                    
                                    <div style={{ display:'flex', flexDirection:'column', alignItems:'center'}}>

                                        <h4 style={{fontSize:'20px', fontFamily:'Roboto, sans-serif', color:'grey'}}>Por favor completa tu perfil para poder comunicarte con otros usuarios...</h4>

                                    </div>
                                    

                               }

                        </div>

                   

                    </div>

                    
                        
                    </div>
                    </Switch>




                </div>


            </div>


            </Router>


            </div>

        </Fragment>

        

    )
}

export default Messenger;
