import React, {Fragment , useState, useEffect} from 'react';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import firebase from "firebase";
import {db} from '../FirebaseDb.js';
import {storage} from '../FirebaseDb';
import SidebarConversations from './sidebarConversations'
import Conversation from './conversation';
import WriteMessage from './writeMessage.jsx';
import Fab from '@material-ui/core/Fab';
import ChatIcon from '@material-ui/icons/Chat';
import Button from '@material-ui/core/Button';
import { Input } from 'semantic-ui-react'
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import EditIcon from '@material-ui/icons/Edit';
import Modal from '@material-ui/core/Modal';
import AddAPhotoIcon from '@material-ui/icons/AddAPhoto';
import AddIcon from '@material-ui/icons/Add';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
  } from "react-router-dom";




        const ChatWindow = () =>{


                        var user = firebase.auth().currentUser;
                        var name, email, photo, uid, emailVerified;

                        name = user.displayName;
                        email = user.email;
                        photo = user.photoURL;
                        emailVerified = user.emailVerified;
                        uid = user.uid;

                     


                        const [input, setInput]=useState("");
                        const [inputR, setInputR]=useState("");
                        const [rooms, setRooms]=useState([]);

                        useEffect(() =>{

                            const unsubscribe = db.collection('rooms').onSnapshot(snapshot =>(

                            setRooms(snapshot.docs.map(doc =>
                            ({

                            id:doc.id,
                            data:doc.data(),

                            }))
                            )
                            ));

                                return () =>{
                                    unsubscribe();
                                }

                    }, [])

                    const [open, setOpen] = React.useState(false);

                    const handleOpen = () => {
                      setOpen(true);
                    };
                  
                    const handleClose = () => {
                      setOpen(false);
                    };


                    const [openM, setOpenM] = React.useState(false);

                    const handleOpenM = () => {
                      setOpenM(true);
                    };
                  
                    const handleCloseM = () => {
                      setOpenM(false);
                    };


                    

                    const [imageProfile, setImageProfile]=useState( null);
                    const [url, setUrl]=useState("");
                    const [progress, setProgress]=useState(0);
                    const [error, setError]=useState("")

                    const handleChange = e =>{
                        const file =e.target.files[0];
                        if (file) {

                        const fileType = file ["type"]
                        const validImageTypes = ["image/gif", "image/jpeg", "image/jpg", "image/png"]
                        if (validImageTypes.includes(fileType)) {

                            setError("");
                            setImageProfile(file);

                        }else{
                            setError("Por favor selecciona una imagen")
                        }
                        }
                    }
                    



                    const createChat = () =>{
                 
                        db.collection("rooms").doc().set({
                            
                            name: inputR,
                            
                        })

                        setInputR("")
                        setOpenM(false)
            
                   
                     }; 





                     const UpDate = () =>{

                        if (imageProfile){

                               const uploadTaks= storage.ref(email).put(imageProfile)
                    
                                uploadTaks.on(
                                    "state_changed",
                                    snapshot =>{
                    
                                        const progress = Math.round(
                                        (snapshot.bytesTransferred / snapshot.totalBytes)* 100
                                     );
                    
                                     setProgress(progress);
                    
                                     },
                                     error => {
                    
                                     setError(error)
                    
                                     },
                                     () => {
                    
                                    storage
                                    .ref(email)
                                    .getDownloadURL()
                                    .then(url =>{
                                        setUrl(url);
                                        setProgress(0);
                                    });
                                    }
                                    );

                    
                                    }else{
                                    setError("")
                                    }

                                    db.collection('profile').doc(email).set({
                                        UserName:input
                                    })

                                    setOpen(false)
                    
                                }

                                const [profilePicurl, setProfilePicurl]=useState();

                                storage.ref(email).getDownloadURL()
                                .then(profilePicurl =>{ setProfilePicurl(profilePicurl); setProgress(0)}); {
            
                                 }


                                 const [myName , setMyName] =useState ("")
                                 useEffect(() =>{

                                    db.collection('profile').doc(email).onSnapshot(snapshot=>(
                                        setMyName(snapshot.data().UserName)
                                    ));
                                        
                        
                                    }, [])



    return(

        <Fragment>

                    <AppBar position="static" style={{backgroundColor:'#3f51b5', width:'100%', height:'50px'}}>
                            <Toolbar variant="dense">
                            
                            <Typography variant="h6" color="inherit">
                                <p id="logoTxtFlepxii">Messenger By React</p>
                            </Typography>

                            </Toolbar>
                   </AppBar>

                   <Modal
                        open={open}
                        onClose={handleClose}
                        aria-labelledby="simple-modal-title"
                        aria-describedby="simple-modal-description"
                        style={{display:'flex', flexDirection:'column', alignItems:'center'}}
                    >

                        <Paper style={{width:'300px', height:'330px', marginTop:'160px', padding:'20px', borderRadius:'20px'}}>

                            <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>

                                    <div>
                                    <Avatar alt="Remy Sharp" src="#" 
                                    style={{boxShadow:'2px 2px 2px rgb(100, 100, 100)',
                                             width:'130px', height:'130px'}} />
                                    </div>
                                                       <div>
                                                            <input
                                                                accept="image/*"
                                                                id="contained-button-file"
                                                                multiple
                                                                type="file"
                                                                onChange={handleChange}
                                                                style={{display:'none'}}
                                                            />
                                                            <label htmlFor="contained-button-file">
                                                            <IconButton aria-label="edit" component="span">
                                                                <AddAPhotoIcon />
                                                            </IconButton>

                                                            </label>

                                                        </div> 

                                    <div style={{marginTop:'10px', display:'flex', flexDirection:'column', alignItems:'center'}}>

                                        <p>Set your name</p>

                                        <Form.Control  type="text" placeholder=" My name"
                                        value={input} onChange={e=>setInput(e.target.value)} style={{}} />

                                    </div>

                                    <div style={{marginTop:'20px'}}>
                                        <Button variant="contained" color="primary" onClick={UpDate}>Save</Button>
                                    </div>

                            </div>

                        

                        </Paper>
                        
                    </Modal>
                    


                    <Modal
                        open={openM}
                        onClose={handleCloseM}
                        aria-labelledby="simple-modal-title"
                        aria-describedby="simple-modal-description"
                        style={{display:'flex', flexDirection:'column', alignItems:'center'}}
                    >

                        <Paper style={{width:'300px', height:'200px', marginTop:'160px', padding:'20px', borderRadius:'20px'}}>

                            <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>

                                    

                                    <div style={{marginTop:'10px', display:'flex', flexDirection:'column', alignItems:'center'}}>

                                        <p style={{fontFamily:'Roboto, sans-serif', fontSize:'20px'}}>Set room name</p>

                                        <Form.Control  type="text" placeholder=" Room name"
                                        value={inputR} onChange={e=>setInputR(e.target.value)} style={{height:'30px'}} />

                                    </div>

                                    <div style={{marginTop:'20px'}}>
                                        <Button variant="contained" color="primary" onClick={createChat}>Create new room</Button>
                                    </div>

                            </div>

                        

                        </Paper>
                        
                    </Modal>

            <div style={{display:'flex', 
                        flexDirection:'row', 
                        alignItems:'center', 
                        backgroundColor:'#d1c4e9', 
                        justifyContent:'space-around',
                        padding:'20px'
                        }}>

            <Router>

                
                    <div style={{marginTop:'0px'}} >

                        <Paper elevation={3} style={{width:'300px', height:'530px', padding:'20px',overflow:'auto', borderRadius:'20px'}}>

                            <div style={{display:'flex', flexDirection:'column', alignItems:'center', marginBottom:'20px'}}>

                                        <div style={{display:'flex', flexDirection:'column', alignItems:'center', width:'100%'}}>

                                             <div style={{display:'flex', flexDirection:'row',alignItems:'center', justifyContent:'space-around', width:'100%'}}>
                                             
                                                    <div style={{display:'flex', flexDirection:'row', alignItems:'center'}}>
                                                    
                                                            <Avatar alt="Remy Sharp" src="#" 
                                                            style={{boxShadow:'2px 2px 2px rgb(100, 100, 100)', 
                                                            width:'60px', height:'60px'}} 
                                                            />

                                                            <p style={{marginLeft:'20px'}}>My Name</p>

                                                    </div>


                                                    <div>

                                                            <IconButton aria-label="edit" onClick={(e)=>setOpen(true)}
                                                            style={{border:'1px solid grey'}}>
                                                                <EditIcon />
                                                            </IconButton>

                                                    </div>

                                            </div>
                                            
                                        </div>

                             </div>

                             <Divider/>

                                    <div style={{display:'flex', flexDirection:'column', alignItems:'center', marginTop:'20px'}}>
                                        <IconButton style={{border:' 1px solid grey'}} onClick={(e)=>setOpenM(true)}>
                                        <AddIcon/>
                                        </IconButton>
                                    </div>

                                    

                                    <div style={{marginTop:'30px'}}>

                                     
                                        {rooms.map(room =>(
                                            <List>
                                                <Link to={`/rooms/${room.id}`}>

                                                    <SidebarConversations key={room.id} id={room.id} name={room.data.name}
                                                    />
                                                
                                                </Link>
                                            </List>
                                        ))}

                                    


                                    </div>

                        </Paper>

                    </div>
                


                    <div style={{display:'flex', flexDirection:'column', alignItems:'center', marginTop:'10px'}}>

                           <div>
                            <Paper style={{width:'700px', height:'460px', borderRadius:'30px', padding:'20px'}}>

                                <Route exact path="/rooms/:roomId" children={<Conversation/> }/>
                               
                            </Paper>
                           </div>

                           <div>
                               <Paper style={{marginTop:'20px', width:'700px'}}>
                               <Route exact path="/rooms/:roomId" children={<WriteMessage/>}/>
                               </Paper>
                           </div>

                    </div>

               </Router>  

            </div>

        </Fragment>

    )
}

export default ChatWindow;
