import React, {Fragment, useState, useEffect} from 'react';
import Avatar from '@material-ui/core/Avatar';
import {db} from '../FirebaseDb.js';
import Button from '@material-ui/core/Button';
import ChatBubbleOutlineIcon from '@material-ui/icons/ChatBubbleOutline';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';



import { Modal } from 'semantic-ui-react'


import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

  import firebase from "firebase";
  import AvatarGroup from '@material-ui/lab/AvatarGroup';
  import IconButton from '@material-ui/core/IconButton';
  import DeleteIcon from '@material-ui/icons/Delete';
  import ExitToAppIcon from '@material-ui/icons/ExitToApp';
 




       const SidebarConversations = ({id,name, integrantes,addNewChat, integrantesPase, admin}) =>{

                        var user = firebase.auth().currentUser;
                        var nam, email, photo, uid, emailVerified;

                        nam = user.displayName;
                        email = user.email;
                        photo = user.photoURL;
                        emailVerified = user.emailVerified;
                        uid = user.uid;


                        const [profile, setProfile] = useState ([])
                        const [yo , setYo]= useState("")
                            
                                React.useEffect(() => {
                                    db.collection(`perfil${email}`).get()
                                    .then(data => {
                                    
                                        setProfile(data.docs.map(doc => {return {...doc.data(), id: doc.id} }))
                                        return profile})
                                        
                                }, [] );



                                const [rooms, setRooms]=useState([]);
                                

                                        useEffect(() =>{

                                                const unsubscribe = db.collection('rooms').onSnapshot(snapshot =>(

                                                setRooms(snapshot.docs.map(doc =>
                                                (

                                                
                                                doc.data().integrantes

                                                ))
                                                )
                                                ));

                                                    return () =>{
                                                        unsubscribe();
                                                        
                                                        
                                                    }

                                    }, [])

                   
                    
    

           

           

            
               

                return  (

        

                    <Fragment>

                    <ListItem button style={{borderRadius:'20px'}} >
                        <div className="sidebar_Conversations" > 

                                    <div>

                                        <AvatarGroup max={4}>

                                            
                                            <Avatar alt="Remy Sharp" src="#" style={{boxShadow:'2px 2px 2px rgb(100, 100, 100)'}} />
                                          
                                            
                                        </AvatarGroup>



                                    </div>

                        
                                  <div style={{display:'flex', flexDirection:'row', alignItems:'center', justifyContent:'space-between', marginTop:'9px'}}>
                                       

                                        <div className="sidebar_conversation_inf">
                            
                                            <h5 style={{color:'white', fontFamily:'Roboto, sans-serif', fontSize:'15px'}}>{name}</h5>
                            
                                        </div>

                                       

                                 </div>


            
                        </div>
               
                        </ListItem>
                    </Fragment>
                    
            
                )
            
        
    
};

export default SidebarConversations;
